function Alarm() {
        var state = {
          currentTime: '',
          alarmTime: '11:12:00',
        };

      function componentDidMount(){
        useEffect(() => {
            setInterval(() => setState({
                                   currentTime: new Date().toLocaleTimeString('ru', { hour12: false })
                                 }), 1000)
            setInterval(
                  () => checkAlarmClock(), 1000)
          }, []);
      }

      function checkAlarmClock(){
