import React, { useState, Component, useEffect, setState, setInterval  } from 'react';
import ReactDOM from "react-dom";
import './App.css';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import { Calendar, momentLocalizer } from 'react-big-calendar'
import moment from 'moment'
import 'moment/locale/ru'
import { Alert, useAlert } from 'react-alert'
import {userName} from './Home';

function MyCalendar()  {

    const localizer = momentLocalizer(moment);
    const [eventsList, setEventsList] = useState([]);

    function handleSelect({ start, end, userName }) {
        const PostCreate = props => (
          <Create {...props}>
            <SimpleForm>
              <ReferenceInput source='userId' reference='users'>
                <SelectInput optionText='name' />
              </ReferenceInput>
              <TextInput source='title' />
              <TextField source='start' />
              <TextField source='end' />
            </SimpleForm>
          </Create>
        );
      };

    return (
        <div>
        <Calendar
        messages={{
                  next: 'Следущий',
                  previous: 'Предыдущий',
                  today: 'Сегодня',
                  month: 'Месяц',
                  week: 'Неделя',
                  day: 'День',
                  yesterday: 'Вчера',
                  tomorrow: 'Завтра',
                  agenda: 'Мероприятия',
                  noEventsInRange: 'Не найдено никаких мероприятий в текущем периоде.',

                  showMore: function showMore(total) {
                    return '+' + total + 'событий';
                  }
        }}
        selectable
        defaultView="week"
        defaultDate={new Date()}
        localizer={localizer}
        events={eventsList}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 500 }}
        onSelectSlot={handleSelect}
        />
        </div>
    )
}

export default MyCalendar;
