import React, { useState, Component, useEffect, setState, setInterval  } from 'react';
import ReactDOM from "react-dom";
import './App.css';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import { Calendar, momentLocalizer } from 'react-big-calendar'
import moment from 'moment'
import 'moment/locale/ru'
import { Alert, useAlert } from 'react-alert'
import {userName} from './Home';
import Clock from 'react-clock';


function MyCalendar()  {

    const localizer = momentLocalizer(moment);
    const [eventsList, setEventsList] = useState([]);

    function handleSelect({ start, end, userName }) {
        const title = window.prompt("New Event name");
        if (title) {
          var newEvent = {
            userName: {userName},
            start: start,
            end: end,
            title: title,
          };
          setEventsList([...eventsList, newEvent]) ;

        }
      };

      function MyApp() {
        const [value, setValue] = useState(new Date());

        useEffect(() => {
          const interval = setInterval(
            () => setValue(new Date()),
            1000
          );

          return () => {
            clearInterval(interval);
          }
        }, []);
       return (
           <div>
             <p>Current time:</p>
             <Clock value={value} />
           </div>
         )
       }

    function Alarm() {
        var state = {
          currentTime: '',
          alarmTime: '11:58:05',
        }
        useEffect(() => {
                   const clock = setInterval(() => setState({
                   currentTime: new Date().toLocaleTimeString('ru', { hour12: false })
                   }), 1000);

                    const interval = setInterval(() => {
                    if(state.currentTime === state.alarmTime) {
                             alert("its time!");
                     } else {
                     console.log("not yet");
                        }}, 1000);
                  })
}


    return (
        <div>
        <Calendar
        messages={{
                  next: 'Следущий',
                  previous: 'Предыдущий',
                  today: 'Сегодня',
                  month: 'Месяц',
                  week: 'Неделя',
                  day: 'День',
                  yesterday: 'Вчера',
                  tomorrow: 'Завтра',
                  agenda: 'Мероприятия',
                  noEventsInRange: 'Не найдено никаких мероприятий в текущем периоде.',

                  showMore: function showMore(total) {
                    return '+' + total + 'событий';
                  }
        }}
        selectable
        defaultView="week"
        defaultDate={new Date()}
        localizer={localizer}
        events={eventsList}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 500 }}
        onSelectSlot={handleSelect}

        />
        <MyApp/>
        </div>
    )
}

export default MyCalendar;
