import React, { useState, Component, useEffect, setState  } from "react";
import ReactDOM from "react-dom";
import './App.css';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import { Calendar, momentLocalizer } from 'react-big-calendar'
import moment from 'moment'
import 'moment/locale/ru'
import { Alert, useAlert } from 'react-alert'
import {userName} from './Home';
import Clock from 'react-clock';

function MyCalendar(alarmTime1: String)  {
    const localizer = momentLocalizer(moment);
    const [eventsList, setEventsList] = useState([]);
    const [seconds, setSeconds] = useState([]);

      var state = {
                  currentTime: new Date().toLocaleTimeString('ru', { hour12: false }),
                }

      useEffect(() => {
        const timer = setInterval(() => {
          setSeconds(
          state.currentTime )
        }, 1000);
        return () => clearInterval(timer);
      });

    function handleSelect({ start, end, userName }) {
        const title = window.prompt("New Event name");
        if (title) {
          var newEvent = {
            userName: {userName},
            start: start,
            end: end,
            title: title,
          };
          setEventsList([...eventsList, newEvent]) ;
          }
          var alarmTime = MyCalendar('14:43:00');
          }

    if(state.currentTime === alarmTime1) {
             alert("its time!");
             } else {
             console.log("not yet");
             }

    return (
        <div>
        <Calendar
        messages={{
                  next: 'Следущий',
                  previous: 'Предыдущий',
                  today: 'Сегодня',
                  month: 'Месяц',
                  week: 'Неделя',
                  day: 'День',
                  yesterday: 'Вчера',
                  tomorrow: 'Завтра',
                  agenda: 'Мероприятия',
                  noEventsInRange: 'Не найдено никаких мероприятий в текущем периоде.',

                  showMore: function showMore(total) {
                    return '+' + total + 'событий';
                  }
        }}
        selectable
        defaultView="week"
        defaultDate={new Date()}
        localizer={localizer}
        events={eventsList}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 500 }}
        onSelectSlot={handleSelect}

        />
        <h1>Number of seconds is {seconds}</h1>
        </div>

    )

}

export default MyCalendar;
