import React, { useState, Component  } from 'react';
import ReactDOM from "react-dom";
import './App.css';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import { Calendar, momentLocalizer } from 'react-big-calendar'
import moment from 'moment'
import 'moment/locale/ru'
import { Alert, useAlert } from 'react-alert'


export function MyCalendar()  {

    const localizer = momentLocalizer(moment)
    const [eventsList, setEventsList] = useState([]);
    const [alarmsList, setAlarmsList] = useState([]);


    function handleSelect({ start, end }) {
        const title = window.prompt("New Event name");
        if (title) {
          var newEvent = {
            start: start,
            end: end,
            title: title,
            alarmTime: '12:50:50',
          };
          setEventsList([...eventsList, newEvent]);
        }
      };

      function checkAlarmClock(){
      var state = {
            currentTime: new Date().toLocaleTimeString('ru', { hour12: false }),
          };
            if(state.currentTime === handleSelect.newEvent.alarmTime) {
              alert('Oh look, an alert!');
            };
          };
    class Alarm extends React.Component {
      constructor() {
        super();
        this.state = {
          currentTime: '',
          alarmTime: '15:43:00'
        };
        this.setAlarmTime = this.setAlarmTime.bind(this);
      }

      componentDidMount(){
        this.clock = setInterval(
          () => this.setCurrentTime(),
          1000
        )
        this.interval = setInterval(
          () => this.checkAlarmClock(),
        1000)
      }

      componentWillUnmount(){
        clearInterval(this.clock);
        clearInterval(this.interval);
      }

      setCurrentTime(){
        this.setState({
          currentTime: new Date().toLocaleTimeString('ru', { hour12: false })
        });
      }

      setAlarmTime(event) {
        event.preventDefault();
        const inputAlarmTimeModified = event.target.value
        this.setState({
          alarmTime: inputAlarmTimeModified
        })
      }

      checkAlarmClock(){
          if(this.state.currentTime === this.state.alarmTime) {
            alert("its time!");
          } else {
            console.log("not yet");
          }
        }

      render() {
        return (
          <div>
            <h2>{this.alarmMessage}</h2>
          </div>
        );
      }
    }
    return (
        <div>
        <Calendar
        messages={{
                  next: 'Следущий',
                  previous: 'Предыдущий',
                  today: 'Сегодня',
                  month: 'Месяц',
                  week: 'Неделя',
                  day: 'День',
                  yesterday: 'Вчера',
                  tomorrow: 'Завтра',
                  agenda: 'Мероприятия',
                  noEventsInRange: 'Не найдено никаких мероприятий в текущем периоде.',

                  showMore: function showMore(total) {
                    return '+' + total + 'событий';
                  }
        }}
        selectable

        defaultView="week"
        defaultDate={new Date()}
        localizer={localizer}
        events={eventsList}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 500 }}
        onSelectSlot={handleSelect}
        />
        {checkAlarmClock}
        </div>
    )
}

export default MyCalendar;
