import React, { useState  } from 'react';
import './App.css';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import { Calendar, momentLocalizer } from 'react-big-calendar'
import moment from 'moment'
import 'moment/locale/ru'
import { Alert, useAlert } from 'react-alert'


export function MyCalendar()  {

    const localizer = momentLocalizer(moment)
    const [eventsList, setEventsList] = useState([]);
    const [alarmsList, setAlarmsList] = useState([]);


    function handleSelect({ start, end }) {
        const title = window.prompt("New Event name");
        if (title) {
          var newEvent = {
            start: start,
            end: end,
            title: title,
            alarmTime: '17:07:00',
          };
          setEventsList([...eventsList, newEvent]);
        }
      };

      function checkAlarmClock(){
      var state = {
            currentTime: new Date().toLocaleTimeString('ru', { hour12: false }),
            alarmTime: '18:03:00'
          };
            if(state.currentTime === state.alarmTime) {
              alert('Oh look, an alert!');
            };
          };

    return (
        <div>
        <Calendar
        messages={{
                  next: 'Следущий',
                  previous: 'Предыдущий',
                  today: 'Сегодня',
                  month: 'Месяц',
                  week: 'Неделя',
                  day: 'День',
                  yesterday: 'Вчера',
                  tomorrow: 'Завтра',
                  agenda: 'Мероприятия',
                  noEventsInRange: 'Не найдено никаких мероприятий в текущем периоде.',

                  showMore: function showMore(total) {
                    return '+' + total + 'событий';
                  }
        }}
        selectable

        defaultView="week"
        defaultDate={new Date()}
        localizer={localizer}
        events={eventsList}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 500 }}
        onSelectSlot={handleSelect}

        />
        <checkAlarmClock/>
        </div>
    )
}

export default MyCalendar;
